import React, { useEffect, useState } from 'react'

import { CircularProgress } from "@material-ui/core"

import axios from "axios"
import TodoCard from '../component/TodoCard'

function Todos() {
  const [todos, setTodos] = useState({})


  const url ="";
  const [TodoForm , setTodoForm] = useState({
    title : "",
    userId : "",
    isCompleted : ""
  });
  function handle(e){
    const newData = {...TodoForm};
    newData[e.target.id] = e.target.value;
    setTodoForm(newData)
    console.log(newData);
  }

  useEffect(() => {
    axios.get('http://localhost:8000/api/todos').then((res) => {
      const responseTodos = res.data;
      setTodos(responseTodos);
    })  
  }, [todos]);
  // let myObj={
  //   name:"roshan",
  //   friends:{
  //     name:"altaf"
  //   }
  // }
  // console.log(myObj?.friends?.name);
  // console.log(todos);
  // console.log("$$$$$$$$$$");
  return (
   <>
   { todos ?.length ?
   (
   <div style={{display:"flex",flexWrap:"wrap"}}>
    {todos.map(todo =>  <TodoCard todo ={todo}/>)}
    <form action="">
      <div>
           <label htmlFor="">Title</label>
          <input onChange={(e) => handle(e)} id="title" value={TodoForm.title} type="text" placeholder='title' /> <br />
      </div>

      <div>
           <label htmlFor="">User ID</label>
          <input onChange={(e) => handle(e)} id="userId" value={TodoForm.userId} type="text" placeholder='User Id' /> <br />
      </div>

      <div>
        <label htmlFor="isCompleted">True : </label>
        <input onChange={(e) => setTodoForm(prev=>({...prev,isCompleted:e.target.checked}))} id="isCompleted" value={TodoForm.isCompleted}  type="radio"  />

        <label htmlFor="isCompleted">False</label>
        <input onChange={(e) => setTodoForm(prev=>({...prev,isCompleted:e.target.checked}))} id="isCompleted" value={TodoForm.isCompleted} type="radio" name='isCompleted' />
      </div>


    </form>
  </div>

   )
    :
    <CircularProgress/>}

   </>
  );
}

export default Todos



