import React from 'react'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const TodoCard = (props) =>{
    const { todo } = props;
    console.log(todo, "######");
    const {_id , title , completed} = todo;
    const navigate = useNavigate();


const deleteTodo = (_id ) =>{
  e.preventDefault();
  axios.delete(`http://localhost:8000/todos/${_id}`).then(res => console.log("deleted" , res )).catch(err => console.log(err));
}

    return (
      <>
        <div style={ {backgroundColor: "grey" ,
         margin: "10px" ,
          padding: '15px',
           width: '150px' }} onClick={() => navigate(`/todo/${_id}`)}>
          <h4> {title} </h4>
          <h6> {` completed ${completed}`}</h6>


        </div>
        <div onClick={() => deleteTodo(_id)}>
            <button ><i class="fa-solid fa-trash-can"></i>Delete</button>
          </div>
        </>
      )
}

export default TodoCard
