import React, {useState , useEffect} from "react";

function Todos(){
  const [todoData , setTodoData] = useState([]);
  useEffect(() =>{
    fetch("http://localhost:8000/api/todos").then((res) =>{
      res.json().then((resp)=>{
        console.warn("result" , resp);
        setTodoData(resp)
      })
    })
  }, [])
  console.warn(todoData)

  return(
    <div>
      <h1>Get API Call</h1>
    </div>
  )
}

export default Todos;





